// ECharts 简单模拟版本
console.log('ECharts placeholder loaded');

// 创建简单的ECharts对象模拟
window.echarts = {
    init: function(dom) {
        console.log('ECharts initialized on', dom.id);
        return {
            setOption: function(option) {
                console.log('ECharts option set:', option);
                dom.innerHTML = '<div style="text-align: center; padding: 20px; color: #333;">' + 
                               option.title.text + '<br/>' + 
                               '地图数据已加载，ECharts图表将在此显示</div>';
            },
            resize: function() {
                console.log('ECharts resized');
            }
        };
    },
    util: {
        mapData: {
            params: {
                geoJson: {
                    features: []
                }
            }
        }
    }
};